package main

import (
	"fmt"
)

var LRUCache map[[2]int]bool = make(map[[2]int]bool)


func isPali (s string, left, right int) bool {
	key := [2]int{left, right}  // get LRU CACHE
	value, has := LRUCache[key]
	if has {
		return value
	}

	if right > left {
		LRUCache[key] = false
		return false
	}

	for ;left < right; left++ {
		if s[left] != s[right] {
			LRUCache[key] = false
			return false
		}
		right --
	}
	LRUCache[key] = true
	return true
}

func DFS(s string, start int, element []string, res *[][]string)  {
	if start >= len(s) {
		// ÊåáÈíàË∂äÁïå ÁªìÊùüÈÄíÂΩí
		t := make([]string, len(element)) // Êñ∞Âª∫‰∏Ä‰∏™Âíå temp Á≠âÈïøÁöÑÂàáÁâá
		copy(t, element) // temp ËøòË¶ÅÂú®ÈÄíÂΩí‰∏≠ÁªßÁª≠Ë¢´‰øÆÊîπÔºå‰∏çËÉΩÂ∞ÜÂÆÉÁöÑÂºïÁî®Êé®ÂÖ• res
		*res = append(*res, t) // Â∞Ü temp ÁöÑÊã∑Ë¥ù Âä†ÂÖ•Ëß£ÈõÜ res
		return
	}

	for i:=start ; i < len(s) ; i++ {
		if isPali(s,start,i) {  // Â¶ÇÊûúÊª°Ë∂≥‰∫ÜÊù°‰ª∂ ÈÇ£Â∞±ÂàáÊéâÂ≠êÊ†ë
			element = append(element, s[start:i+1]) // ÂàáÂâ≤ ÂºÄÂßãÈÄíÂΩí
			DFS(s, i+1, element, res) // ‰ªé i ÂºÄÂßãÊé•ÁùÄÂæÄÊ∑±Â§ÑÊé¢Á¥¢
			element = element[:len(element)-1] // ÈÄíÂΩíÁªìÊùü Êí§ÈîÄÊîπÂä®Ôºå‰∏ã‰∏ÄËΩÆËø≠‰ª£
		}
	}
}

func partition(s string) [][]string {
	var res [][]string
	DFS(s, 0, make([]string, 0), &res)
	return res
}

func main() {
	s := partition("ab")
	fmt.Println(s)
	fmt.Println(LRUCache)
}

