document.onkeydown=function (e){
    console.log(e.key);
    switch (e.key){
        case "ArrowLeft":
            dx-=5;
            break;
        case "ArrowRight":
            dx+=5;
            break;
        case "ArrowUp":
            dy-=5;
            break;
        case "ArrowDown":
            dy+=5;
            break;
    }
}

document.onkeyup=function (e){
    console.log(e.key);
    switch (e.key){
        case "ArrowLeft":
            dx=0;
            break;
        case "ArrowRight":
            dx=0;
            break;
        case "ArrowUp":
            dy=0;
            break;
        case "ArrowDown":
            dy=0;
            break;
    }
}


