#include "stdio.h"
#include "stdlib.h"

#define OK 1
#define ERROR 0
#define CAPACITY 10

typedef int Status;
typedef int ElementType;

Status initQueue();
Status isEmpty();
Status isFull();
Status enQueue();
Status deQueue();

typedef struct
{
  ElementType *array;
  int front, rear;
  int capacity;
  int size;
} *Queue;

int main(void)
{
  Queue q = NULL;
  initQueue(&q);
  enQueue(q, 1);
  enQueue(q, 2);
  enQueue(q, 3);
  int foo;
  deQueue(q, &foo);
  deQueue(q, &foo);
  deQueue(q, &foo);
  printf("%d", foo);
  return 0;
}

Status initQueue(Queue *q)
{
  (*q) = (Queue)malloc(sizeof(Queue));
  (*q)->capacity = CAPACITY;
  printf("capacity = %d\n", (*q)->capacity);
  (*q)->array = (ElementType *)malloc((*q)->capacity * sizeof(ElementType));
