func (a *Loger) InfoLog(wireteString string,extfilename string){


	var f    *os.File
	var err   error

	var filename = "info_"+extfilename + getCurDate()+".log"

	if checkFileIsExist(a.path+filename) {  //å¦‚æžœæ–‡ä»¶å­˜åœ¨
		f, err = os.OpenFile(a.path+filename,  os.O_APPEND|os.O_WRONLY, os.ModeAppend)  //æ‰“å¼€æ–‡ä»¶
		//fmt.Println("æ–‡ä»¶å­˜åœ¨");
	}else {
		f, err = os.Create(a.path+filename)  //åˆ›å»ºæ–‡ä»¶
		//fmt.Println("æ–‡ä»¶ä¸å­˜åœ¨");
	}


	check(err)

	wireteString = wireteString+"\r\n"
	_,err = io.WriteString(f, wireteString) //å†™å…¥æ–‡ä»¶(å­—ç¬¦ä¸²)
	check(err)
	//æ ¼å¼åŒ–ç”¨çš„æ—¥æœŸæ˜¯ç‰¹å®šçš„ï¼Œ123 ï¼ˆ 15 ï¼‰ 45 -.-lll
	now_time := time.Now().Format("2006-01-02 15:04:05")
	io.WriteString(f, now_time+"\r\n") //å†™å…¥æ–‡ä»¶(å­—ç¬¦ä¸²)
	//fmt.Printf("å†™å…¥ %d ä¸ªå­—èŠ‚", n);


	return
}

