  function fnGetCDNUrl(url) {
    const arrMap = [
      ["https://github.com/", "https://cdn.jsdelivr.net/gh/"],
      ["/blob/", "@"]
    ]
    let cdnUrl = url;
    arrMap.forEach(line => {
      cdnUrl = cdnUrl.replace(line[0], line[1]);
    });
    return cdnUrl;
  }
